import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      if (password !== confirmPassword) {
        setError("Les mots de passe ne correspondent pas");
        return;
      }
      const response = await axios.post('http://localhost:5000/api/user', { 
        username, 
        email, 
        password, 
        confirmPassword 
      });
      console.log('Inscription réussie', response.data);
    } catch (error) {
      console.error('Erreur lors de l\'inscription :', error);
      if (error.response) {
        console.error('Le serveur a répondu avec des données d\'erreur :', error.response.data);
        console.error('Le serveur a répondu avec le statut d\'erreur :', error.response.status);
        setError(error.response.data.message); 
      } else if (error.request) {
        console.error('Aucune réponse reçue du serveur :', error.request);
        setError('Aucune réponse reçue du serveur');
      } else {
        console.error('Erreur lors de la configuration de la requête :', error.message);
        setError('Erreur lors de la configuration de la requête');
      }
    } finally {
      setLoading(false);
    }
  };  

  return (
    <div className="container">
      <div className="form-container register">
        <form onSubmit={handleSubmit}>
          <h1>Inscription</h1>
          <div className="social-icons">
            <a><i className="fa-brands fa-facebook"></i></a>
            <a><i className="fa-brands fa-linkedin"></i></a>
            <a><i className="fa-brands fa-google"></i></a>
            <a><i className="fa-brands fa-github"></i></a>
          </div>
          <span>Inscription avec Email et Mots de passe</span>
          <input
            className="form-input"
            type="text"
            placeholder="Nom"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            className="form-input"
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            className="form-input"
            type="password"
            placeholder="Mots de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <input
            className="form-input"
            type="password"
            placeholder="Confirmé le mots de passe"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
          {error && <div className="error-message">{error}</div>}
          <button type="submit" disabled={loading}>{loading ? 'Registering...' : 'Inscription'}</button>
          <Link to="/login" className="login-link">Vous avez un compte ? Connectez-vous</Link>
        </form>
      </div>
    </div>
  );
};

export default Register;
