import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, Container, Row, Col, Spinner, Button } from 'react-bootstrap';
import './custom.css';

const Favoris = () => {
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFavorites = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/favoris');
        setFavorites(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Erreur lors de la récupération des favoris', error);
      }
    };

    fetchFavorites();
  }, []);

  return (
    <Container className="text-center mt-4">
      <Row>
        <Col>
          <h1 className="my-4 text-center">Mes Favoris</h1>
        </Col>
      </Row>
      {loading ? (
        <Row>
          <Col className="text-center">
            <Spinner animation="border" role="status">
              <span className="sr-only">Chargement...</span>
            </Spinner>
          </Col>
        </Row>
      ) : (
        <Row>
          {favorites.map((book) => (
            <Col key={book._id} md={4} className="mb-4">
              <Card className="h-100 shadow-sm">
                <Card.Body>
                  <Card.Title>{book.title}</Card.Title>
                  <Card.Text>
                    <strong>Auteur:</strong> {book.author}<br />
                    <strong>Pages:</strong> {book.pages}<br />
                  </Card.Text>
                  <Button className="btn-custom mt-3">Voir plus</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
};

export default Favoris;
